class Solution:
    def getSubArrays(self, arr: list) -> list:
        subarrays = [[]]
        for i in range(len(arr) + 1):
            for j in range(i):
                subarrays.append(arr[j: i])
        return subarrays

    def numOfSubarrays(self, arr: list) -> int:
        oddCount = 0
        subarrays = self.getSubArrays(arr)
        for sub in subarrays:
            if sum(sub) % 2 != 0:
                oddCount += 1
        return oddCount % ((10**9) + 7)

